import React from 'react'
import {Center, Text, CheckIcon, Icon, HStack, Checkbox, CheckboxGroup, CheckboxIndicator, CheckboxIcon, CheckboxLabel, Heading, VStack, RemoveIcon, Box, FormControl} from '@gluestack-ui/themed'

function CheckboxGroupBasic() {
const [values, setValues] = React.useState(['Label 1'])

  return (
<CheckboxGroup accessibilityLabel="Checkbox Group" value={values} onChange={setValues} nativeID="checkbox-group">
      <Checkbox m="\$2" size="$1" isInvalid="$1" isIndeterminate value="Label 1" aria-label="Label 1" accessibilityLabel="Checkbox" onChange={(isSelected: boolean) =>

  console.log(isSelected, '###')} nativeID="checkbox-1">
        <CheckboxIndicator mr="\$2">
          <CheckboxIcon as={CheckIcon} />
        </CheckboxIndicator>
        <CheckboxLabel>Label 1</CheckboxLabel>
      </Checkbox>
      <Checkbox m="\$2" isInvalid="$1" size="$1" aria-label="Label 2" value="Label 2" accessibilityLabel="Checkbox" onChange={(isSelected: boolean) =>

  console.log(isSelected, '###')} nativeID="checkbox-2">
        <CheckboxIndicator mr="\$2">
          <CheckboxIcon as={CheckIcon} />
        </CheckboxIndicator>
        <CheckboxLabel>Label 2</CheckboxLabel>
      </Checkbox>
    </CheckboxGroup>
  );
}

export default CheckboxGroupBasic;
```

