import {Center, ChevronDownIcon, Select, SelectBackdrop, SelectContent, SelectDragIndicator, SelectDragIndicatorWrapper, SelectIcon, SelectInput, SelectItem, SelectPortal, SelectTrigger, Icon, FormControl, FormControlLabel, FormControlLabelText, FormControlHelper, FormControlHelperText, FormControlError, FormControlErrorIcon, FormControlErrorText, AlertCircleIcon} from '@gluestack-ui/themed'

function SelectWithFormControl-underlined() {
const items = colors.map(c => {
  return <SelectItem key={c.value} label={c.label} value={c.value} isDisabled={c.isDisabled} />;
})
const [selected, setSelected] = React.useState(colors[0])

  return (
<FormControl size={"md"} variant={"underlined"} isInvalid={false} isDisabled={false}>
      <FormControlLabel>
        <FormControlLabelText>Choose your favorite color</FormControlLabelText>
      </FormControlLabel>
      <Select selectedValue={selected.value} selectedLabel={selected.label} onValueChange={value => {
    setSelected(colors.filter(c => c.value === value)[0]);
  }}>
        <SelectTrigger size={size} variant={variant}>
          <SelectInput placeholder="Select option" />
          <SelectIcon mr="\$3">
            <Icon as={ChevronDownIcon} />
          </SelectIcon>
        </SelectTrigger>
        <SelectPortal>
          <SelectBackdrop />
          <SelectContent>
            <SelectDragIndicatorWrapper>
              <SelectDragIndicator />
            </SelectDragIndicatorWrapper>
            {items}
          </SelectContent>
        </SelectPortal>
      </Select>
      <FormControlHelper>
        <FormControlHelperText>
          You can only select one option
        </FormControlHelperText>
      </FormControlHelper>
      <FormControlError>
        <FormControlErrorIcon>
          <Icon as={AlertCircleIcon} />
        </FormControlErrorIcon>
        <FormControlErrorText>Mandatory field</FormControlErrorText>
      </FormControlError>
    </FormControl>
  );
}

